import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Student } from 'src/app/shared/models/student.model';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-student-list',
  templateUrl: './student-list.component.html',
  styleUrls: ['./student-list.component.css']
})
export class StudentListComponent implements OnInit {
  
  searchForm: any;
  searchTerm: string = '';
  filterList: Student[] = [];
  students: Student[] = [
    {picture: "", firstname:"Aline", lastname:"Ammann", street:"Strasse 1", city: "3718 Kandersteg", email: "aline@gmail.com"},
    {picture: "", firstname:"Lukas", lastname:"Küffer", street:"Strasse 2", city: "3174 Thörishus", email: "lukas@gmail.com"},
    {picture: "../../../../assets/graduated.png", firstname:"Svenja", lastname:"Berger", street:"Strasse 3", city: "Thun", email: "svenja@gmail.com"},
    {picture: "../../../../assets/graduated.png", firstname:"Anna", lastname:"Müller", street:"Strasse 4", city: "Bern", email: "anna@gmail.com"},
    {picture: "../../../../assets/graduated.png", firstname:"Manuel", lastname:"Hofer", street:"Strasse 5", city: "Luzern", email: "manuel@gmail.com"},
    {picture: "../../../../assets/graduated.png", firstname:"Nina", lastname:"Ammann", street:"Strasse 6", city: "Frutigen", email: "nina@gmail.com"},
  ];
  activeList = this.students;
  filter_active = false;

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.searchForm = this.formBuilder.group({
      searchTerm: ['']
    })
  }
  
  filter() {
    this.searchTerm = this.searchForm.get('searchTerm').value.toLocaleLowerCase();
    this.filterList = [];
    this.filter_active = true;
    this.filterList = this.students.filter((student) => 
        student.lastname.toLocaleLowerCase().indexOf(this.searchTerm) !== -1 ||
        student.firstname.toLocaleLowerCase().indexOf(this.searchTerm)  !== -1 ||
        (student.lastname.toLocaleLowerCase() + ' ' + student.firstname.toLocaleLowerCase()).indexOf(this.searchTerm) !== -1 ||
        (student.firstname.toLocaleLowerCase() + ' ' + student.lastname.toLocaleLowerCase()).indexOf(this.searchTerm) !== -1);
    this.activeList = this.filterList;
  }

  clearFilter() {
    this.searchTerm = '';
    this.filter_active = false;
    this.activeList = this.students;
  }
}
